RIGHT, DOWN = 3, 1

def ski(data, right, down):
    mountain = []
    # convert data to 2d array
    for row in data:
        mountain.append(list(row))
    
    x, y = 0, 0
    tree_count = 0
    
    while y < len(mountain):
        if x > len(mountain[0]) - 1:
            x = x - len(mountain[0])
        
        if mountain[y][x] == '#':
            print(f"TREE: ({x}, {y})")
            tree_count += 1
        else:
            print(f"SAFE: ({x}, {y})")
        
        x += right
        y += down

    return tree_count

if __name__ == "__main__":
    tree_count = ski(DATA, RIGHT, DOWN)
