import sys
sys.setrecursionlimit(10000)

from pathfinding.core.diagonal_movement import DiagonalMovement
from pathfinding.core.grid import Grid
from pathfinding.finder.a_star import AStarFinder

class ZGrid(Grid):
    def __init__(self, width=0, height=0, matrix=None, inverse=False):
        super().__init__(width=width, height=height, matrix=matrix, inverse=inverse)

    def walkable(self, to_x, to_y, from_x, from_y):
        return self.inside(to_x, to_y) and self.nodes[to_y][to_x].walkable and self.nodes[to_y][to_x].weight - self.nodes[from_y][from_x].weight <= 1

    def neighbors(self, node, diagonal_movement=DiagonalMovement.never):
        """
        get all neighbors of one node
        :param node: node
        """
        x = node.x
        y = node.y
        neighbors = []

        # ↑
        if self.walkable(x, y - 1, x, y):
            neighbors.append(self.nodes[y - 1][x])
        # →
        if self.walkable(x + 1, y, x, y):
            neighbors.append(self.nodes[y][x + 1])
        # ↓
        if self.walkable(x, y + 1, x, y):
            neighbors.append(self.nodes[y + 1][x])
        # ←
        if self.walkable(x - 1, y, x, y):
            neighbors.append(self.nodes[y][x - 1])

        return neighbors

def run(data, debug=False):
    current = ()
    destination = ()
    grid = []
    
    for row in data:
        grid.append(list(row))
    
    for y in range(len(grid)):
        for x in range(len(grid[y])):
            if grid[y][x] == "S":
                grid[y][x] = "a"
                current = (x, y)
            elif grid[y][x] == "E":
                grid[y][x] = "z"
                destination = (x, y)
            grid[y][x] = ord(grid[y][x]) - ord('a') + 1
            
    print("Checking Start", current, "End", destination)

    grid = ZGrid(matrix=grid)
    
    start = grid.node(current[0], current[1])
    end = grid.node(destination[0], destination[1])
    
    finder = AStarFinder(diagonal_movement=DiagonalMovement.never)
    path, runs = finder.find_path(start, end, grid)

    if debug: print('operations:', runs, 'path length:', len(path))
    if debug: print(grid.grid_str(path=path, start=start, end=end))

    return len(path) - 1

